      expect -timeout 10 "In "
      send "\u0004"
      expect -timeout 10 "exit"
      send "y\r"
    EOS
    output = shell_output("expect -f console.exp")
    assert_match "JJava kernel #{version}", output
    assert_match "Hello world!", output
  end
end
